--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "BN_SATE"."BNPD_12_ASIGNACION_ACT" 
IS
BEGIN

    -- 1. Utiliza la sentencia MERGE para combinar datos de las tablas y actualizarlos o insertarlos en la tabla de destino
    MERGE INTO BN_SATE.BNSATE04_ASIGNACION cas
    USING (
        -- 2. Selecciona los datos de las tablas que se usarán para la combinación
        SELECT 
            tar.B05_ID_TAR,                             -- ID de la tarjeta
            SUBSTR(rpt.B12_NUMERO_CUENTA, 4, 16) AS B05_NUM_CUENTA,  -- Extrae el número de cuenta
            rpt.B12_CODIGO_ASIGNACION,                 -- Código de asignación del reporte
            rpt.B12_FECHA_INICIO_LINEA,                -- Fecha de inicio de línea del reporte
            rpt.B12_FECHA_FIN_LINEA                    -- Fecha de fin de línea del reporte
        FROM BN_SATE.BNSATE05_TARJETA tar
        JOIN BN_SATE.BNSATE12_RPTA_MC_TEMP rpt 
        ON tar.B05_NUM_CUENTA = SUBSTR(rpt.B12_NUMERO_CUENTA, 4, 16)  -- Condición de unión
        WHERE rpt.B12_TIPO_RESPUESTA = '3'                         -- Filtra por tipo de respuesta
        AND rpt.B12_FECHA_REGISTRO = (
            -- 3. Subconsulta para obtener la fecha de registro más reciente
            SELECT MAX(B12_FECHA_REGISTRO) 
            FROM BN_SATE.BNSATE12_RPTA_MC_TEMP tem 
            WHERE rpt.B12_NUMERO_CUENTA = tem.B12_NUMERO_CUENTA 
            AND tem.B12_TIPO_RESPUESTA = '3'
        )
    ) rptar
    
    ON (cas.B04_CODIGO_ASIGNACION = rptar.B12_CODIGO_ASIGNACION)  -- Condición de coincidencia
