                    'R' || 'R' || CASE
                        WHEN tar.B05_PORCENTAJE_DISP_EFECT = '00000' THEN ''
                        ELSE RPAD(NVL(tar.B05_PORCENTAJE_DISP_EFECT, '0'), 7, '0')
                        END, 926, ' ') AS linea
        FROM BNSATE04_ASIGNACION asi
                 JOIN
